/**
 * 供 ttsa 等使用：从 protobufjs 挂到全局 protobuf
 * face_data_pb.js 依赖全局 protobuf
 */
const protobuf = require('protobufjs/minimal');
if (typeof global !== 'undefined') global.protobuf = protobuf;
if (typeof globalThis !== 'undefined') globalThis.protobuf = protobuf;
if (typeof window !== 'undefined') window.protobuf = protobuf;
module.exports = protobuf;
